  toggelDiv = (type) => {
    // alert("Great Shot!");
    var data = this.state;
    if (type == "f") {
      data.fDivState = !data.fDivState;
    } else {
      data.cDivState = !data.cDivState;
    }
    this.setState(data);
  };
  render() {
    return (
      <div>
        <h1>Styling using Functional and Class Component</h1>
        <div>
          <p onClick={this.toggelDiv("f")}>
            To see styling in functional component
          </p>
          <p onClick={this.toggelDiv("c")}>To see styling in class component</p>
        </div>
        {/* <Functional />
        <Classcomp /> */}
      </div>
    );
  }
}
export default App;
